import React, { useState, useEffect } from 'react';
import './Slideshow.css'; // You would create this CSS file to style your slideshow

function Slideshow({ features }) {
    const [currentFeatureIndex, setCurrentFeatureIndex] = useState(0);

    useEffect(() => {
    const intervalId = setInterval(() => {
    setCurrentFeatureIndex((currentFeatureIndex + 1) % features.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(intervalId); // Clean up the interval on unmount
    }, [currentFeatureIndex, features.length]);

    const { title, description, image } = features[currentFeatureIndex];

    return (
    <div className="slideshow">
        <img src={image} alt={title} className="slideshow__image" />
        <h3 className="slideshow__title">{title}</h3>
        <p className="slideshow__description">{description}</p>
    </div>
    );
}

export default Slideshow;
