import React, { useState, useEffect } from 'react';
import './DarkModeToggle.css';


const getCookie = (name) => {
    const cookieName = name + "=";
    const cookieArray = document.cookie.split(';');
    for (let i = 0; i < cookieArray.length; i++) {
        let cookie = cookieArray[i];
        while (cookie.charAt(0) === ' ') {
            cookie = cookie.substring(1);
        }
        if (cookie.indexOf(cookieName) === 0) {
            return cookie.substring(cookieName.length, cookie.length);
        }
    }
    return "";
}

function DarkModeToggle() {
    console.log('DarkModeToggle(): called')
    const [isDarkMode, setIsDarkMode] = useState(false); // State to track dark mode status

    useEffect(() => {
        const storedDarkMode = getCookie('dark-mode');
        setIsDarkMode(storedDarkMode === '1');
        toggleDarkMode(storedDarkMode === '1');
    }, []);



    const setCookie = (name, value, days) => {
        console.log('DarkModeToggle(): setCookie() called')
        const date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        const expires = "expires=" + date.toUTCString();
        document.cookie = name + "=" + value + ";" + expires + ";path=/";
    }

    const toggleDarkMode = (value) => {
        console.log('DarkModeToggle(): toggleDarkMode() called')
        document.body.classList.toggle('dark-mode', value);
        const elements = document.querySelectorAll(
            'html, body, p, div, label, span, nav, Link, ul, h1, h2, .background, .navbar, .navbar__list, .navbar__link'
            );
        elements.forEach(element => {
            element.classList.toggle('dark-mode', value);
        });
    }

    const handleToggleChange = () => {
        console.log('DarkModeToggle(): handleToggleChange() called')
        const newValue = !isDarkMode;
        setIsDarkMode(newValue);
        setCookie('dark-mode', newValue ? '1' : '0', 30); // Assuming cookie should last for 30 days
        toggleDarkMode(newValue);
    }

    return (
        <>
            {/* Other components and JSX here */}
            <input type="checkbox" id="dark-mode" checked={isDarkMode} onChange={handleToggleChange} />
        </>
    );
}

export default DarkModeToggle;
export { getCookie };
