// animateLines.js
import { gsap } from 'gsap';

export const AnimateLines = (containerSelector) => {
  const container = document.querySelector(containerSelector);
  const numberOfLines = 8;
  const angleIncrement = 360 / numberOfLines;

  for (let i = 0; i < numberOfLines; i++) {
    const line = document.createElement('div');
    line.classList.add('line');
    // Set initial position and rotation of the line
    line.style.position = 'absolute';
    line.style.top = '50%';
    line.style.left = '50%';
    line.style.width = '4px';
    line.style.height = '30px';
    line.style.background = 'black';
    line.style.transformOrigin = 'bottom';
    line.style.transform = `translate(-50%, -50%) rotate(${i * angleIncrement}deg) scale(0)`;

    container.appendChild(line);
    // Animate line from scale 0 to scale 1 and then fade out
    gsap.to(line, {
      scale: 1,
      opacity: 1,
      duration: 0.5,
      ease: 'elastic.out(1, 0.3)',
      delay: i * 0.1, // Stagger the animation of each line
      onComplete: () => {
        // Fade out after popping in
        gsap.to(line, { opacity: 0, duration: 0.5, delay: 0.7 });
      }
    });
  }
};
