import React, { useState, useEffect, useRef } from 'react';
import './Slideshow.css';

const Slideshow = ({ slides }) => {
  // State for the current slide index
  const [currentSlide, setCurrentSlide] = useState(0);

  // State to track the previous slide for animation purposes
  const [prevSlide, setPrevSlide] = useState(null);

  // Reference for the interval to enable automatic slide change
  const intervalRef = useRef(null);

  // Function to move to the next slide
  const nextSlide = () => {
    // Update the previous slide index before changing the current slide
    setPrevSlide(currentSlide);
    // Change to the next slide, wrapping around if at the end
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  // Function to generate the slide's style, especially for GIFs
  const getSlideStyle = (slide, index) => {
    let backgroundImage = `url(${slide.image})`;

    // If the slide is a GIF and it's the current slide, add a unique query string to reset the GIF
    if (slide.isGif && index === currentSlide) {
      const uniqueString = new Date().getTime();
      backgroundImage = `url(${slide.image}?unique=${uniqueString})`;
    }

    return { backgroundImage };
  };

  // useEffect hook to set and clear the interval for auto slide changing
  useEffect(() => {
    // Set an interval for the slide change
    intervalRef.current = setInterval(nextSlide, 3000);

    // Cleanup function to clear the interval when the component unmounts
    return () => clearInterval(intervalRef.current);
  }, [currentSlide, slides.length]);

  // Render the slideshow component
  return (
    <div className="slideshow">
      {slides.map((slide, index) => (
        <div
          key={index}
          className={`slide ${index === currentSlide ? 'active' : ''} ${index === prevSlide ? 'previous' : ''}`}
          style={getSlideStyle(slide, index)}
        >
          <div className="caption">{slide.caption}</div>
        </div>
      ))}
    </div>
  );
};

export default Slideshow;
