// animateLines.js
import { gsap } from 'gsap';

export const AnimateLines = (containerSelector) => {
  const container = document.querySelector(containerSelector);
  const numberOfLines = 8;
  const angleIncrement = 360 / numberOfLines;

  for (let i = 0; i < numberOfLines; i++) {
    const line = document.createElement('div');
    line.classList.add('line');
    container.appendChild(line);
    gsap.fromTo(line,
      {
        scale: 0,
        rotation: i * angleIncrement,
        transformOrigin: 'bottom center',
      },
      {
        scale: 1,
        opacity: 1,
        duration: 0.5,
        ease: 'elastic.out(1, 0.3)',
        onComplete: () => gsap.fromTo(line, {
            scale: 0,
            // Ensure the rotation is applied as a CSS property
            css: { rotation: i * angleIncrement },
            transformOrigin: '50% 100%', // Adjust this line
          }, {
            scale: 1,
            opacity: 1,
            duration: 0.5,
            ease: 'elastic.out(1, 0.3)',
            onComplete: () => gsap.to(line, { opacity: 0, duration: 0.5 })
          }
        )
      }
    );
  }
};



export default AnimateLines;
