import React, { useState } from 'react';
import './Slideshow.css';

const Slideshow = ({ slides }) => {
    const [current, setCurrent] = useState(0);

    // Function to go to next slide
    const nextSlide = () => {
        setCurrent(current === slides.length - 1 ? 0 : current + 1); // If we're at the last slide, go back to the first one
    };

    // Function to go to previous slide
    const prevSlide = () => {
        setCurrent(current === 0 ? slides.length - 1 : current - 1); // If we're at the first slide, go to the last one
    };

    return (
    <div className="slideshow">
        <div className="slideshow-visuals">
            <div class="ss__arrow-slide-wrapper">
                <button className="left-arrow" onClick={prevSlide}>Prev</button>
                {slides.map((slide, index) => (
                <div
                    className={index === current ? 'slide active' : 'slide'}
                    key={index}
                    style={{ opacity: index === current ? 1 : 0.5, transform: index < current ? 'rotateY(-30deg)' : index > current ? 'rotateY(30deg)' : 'rotateY(0deg)' }}
                >
                    {index === current && (
                    <img src={slide.image} alt={`Slide ${index}`} />
                    )}
                </div>
                ))}
                <button className="right-arrow" onClick={nextSlide}>Next</button>
            </div>
        </div>
        <div className="slide__description">
            <p>{slides[current].description}</p>
        </div>
    </div>
    );
};

export default Slideshow;
