import React, { useState, useEffect } from 'react';
import ReactRotatingText from 'react-rotating-text';

function Home() {
    const [isAnimating, setIsAnimating] = useState(false);

    useEffect(() => {
        const interval = setInterval(() => {
            setIsAnimating(true);
            setTimeout(() => setIsAnimating(false), 1000); // Match this timing with your animation duration
        }, 3000); // Match this with the rotation interval of ReactRotatingText

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="home__text-container">
            <h1 className="home__text-name">Kevin Cendana</h1>
            <p className={`home__text-alias ${isAnimating ? 'animate-out' : ''}`}>
                <ReactRotatingText items={['Computer Science Student', 'Aspiring Web Developer', 'Enthusiastic Programmer']} />
            </p>
        </div>
    );
}


export default Home;
