import React, { useState, useEffect, useRef } from 'react';
import './Slideshow.css';

const Slideshow = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slideshowRef = useRef();
  const intervalRef = useRef(null); // Ref for the interval to allow for cleanup

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const resetInterval = () => {
    clearInterval(intervalRef.current); // Clear any existing interval
    intervalRef.current = setInterval(nextSlide, 3000); // Set a new interval
  };

  useEffect(() => {
    resetInterval(); // Set up the interval for auto slide change when the component mounts

    // Clear the interval when the component unmounts
    return () => clearInterval(intervalRef.current);
  }, [slides.length]); // Dependency on slides.length to reset interval if slide count changes

  const handleDragStart = (e) => {
    const startX = e.clientX;
    clearInterval(intervalRef.current); // Clear interval on user interaction

    slideshowRef.current.onmousemove = (ev) => {
      if (ev.clientX - startX > 100) {
        setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
        slideshowRef.current.onmousemove = null;
      } else if (startX - ev.clientX > 100) {
        setCurrentSlide((prev) => (prev + 1) % slides.length);
        slideshowRef.current.onmousemove = null;
      }
    };
  };

  const handleDragEnd = () => {
    slideshowRef.current.onmousemove = null;
    resetInterval(); // Restart the interval after user interaction
  };

  return (
    <div
      className="slideshow"
      ref={slideshowRef}
      onMouseDown={handleDragStart}
      onMouseUp={handleDragEnd}
      onMouseLeave={handleDragEnd}
    >
      {slides.map((slide, index) => (
        <div
          key={index}
          className={`slide ${index === currentSlide ? 'active' : ''}`}
        >
          <img src={slide.image} alt={slide.caption} />
          <div className="caption">{slide.caption}</div>
        </div>
      ))}
    </div>
  );
};

export default Slideshow;
