//--------------------------------------------------------------------------------------//
//                                       Projects.js                                       //
//--------------------------------------------------------------------------------------//


import React from 'react';
import Slideshow from '../Slideshow/Slideshow';
import DarkModeToggle from '../DarkModeToggle/DarkModeToggle';
import './Projects.css'; 
import personal_project1 from '../images/project_images/maplestory_app.gif';
import personal_project2 from '../images/project_images/invaded_space.gif';
import personal_project3 from '../images/project_images/bullseye.png';
import hackathon1 from '../images/project_images/lyric_link.gif';
import hackathon2 from '../images/project_images/att_empowher.gif';

// array of slides to use for slideshow pictures
const hackathon_slides = [
  { image: hackathon1, caption: 'SacHacks' },
  { image: hackathon2, caption: 'AT&T: EmpowHer' },
  { image: personal_project3, caption: 'Bullseye' },
];

const personal_projects_slides = [
  { image: personal_project1, caption: 'Making Maplestory w/ Flutter' },
  { image: personal_project2, caption: 'Invaded Space' },
  { image: personal_project3, caption: 'Bullseye' },
];

function Projects() {
  return (
    <div className="projects">
      {/*section: hackathons*/}
      <div className="projects-section">
        <div className="projects__slideshow-container">
          <Slideshow slides={hackathon_slides} />
        </div> 
        <div className="projects-section-text">
          <h2>Hackathons</h2>
          <div>
            <p>• <a href="http://sachacks.com/" target="_blank" rel="noopener noreferrer">SacHacks</a></p>
            <p>• AT&T: EmpowHer</p>
            <p>• DataFest</p>
          </div>
        </div>
      </div>



    </div>
  );
}
export default Projects;
