// import the main react dependency and the components
import React from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';  

import './App.css';
import Home from './Home/Home';
import About from './About';
import Contact from './Contact';

function App() {
  return (
    <Router> {/* use the router component to enable client-side routing. */}
      <div className = "background">
        <div className="navbar">
          <ul className="navbar__list">
            <li className="navbar__item"><Link to="/">Home</Link></li>
            <li className="navbar__item"><Link to="/about">About</Link></li>
            <li className="navbar__item"><Link to="/contact">Contact</Link></li>
          </ul>
        </div>
        {/* define the routes (URL paths) and their corresponding components. */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>

      </div>
    </Router>
  );
}

export default App;
