import React, { useState, useEffect, useRef } from 'react';
import './Slideshow.css';
const getSlideStyle = (slide, index) => {
    let backgroundImage = `url(${slide.image})`;

    // If the slide is a GIF and is the current slide, append a unique query string
    if (slide.isGif && index === currentSlide) {
      const uniqueString = new Date().getTime(); // Current timestamp
      backgroundImage = `url(${slide.image}?unique=${uniqueString})`;
    }

    return { backgroundImage };
  };
const Slideshow = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [prevSlide, setPrevSlide] = useState(null); // Track the previous slide
  const intervalRef = useRef(null);

  const nextSlide = () => {
    setPrevSlide(currentSlide); // Set the current slide as previous before changing
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  // Set up interval for auto slide change
  useEffect(() => {
    intervalRef.current = setInterval(nextSlide, 3000);
    return () => clearInterval(intervalRef.current);
  }, [currentSlide, slides.length]);

  return (
    <div className="slideshow">
      {slides.map((slide, index) => (
        <div
          key={index}
          className={`slide ${index === currentSlide ? 'active' : ''} ${index === prevSlide ? 'previous' : ''}`}
          style={{ backgroundImage: `url(${slide.image})` }}
        >
          <div className="caption">{slide.caption}</div>
        </div>
      ))}
    </div>
  );
};

export default Slideshow;
