//                                         Home                                         //
//--------------------------------------------------------------------------------------//
// this is the default home page of the website, the first page that the user sees

import React, { useEffect } from 'react';
import './Home.css';
import '../App.css';
import './TypingDots.css';
import TypingDots from './TypingDots';
import { AnimateLines } from '.AanimateLines';

function Home() {
    const [showHomeContent, setShowHomeContent] = React.useState(true); //state for managing the display of typing dots
    useEffect(() => {
        // Trigger the line animation when the component mounts
        AnimateLines('.home__left-image-container');
    }, []);
    return (
        <div className="home">
            <div className="home__left animated">
                <div className="home__left-image-container"> 
                <span></span>
                </div>
                <div className="home__text-container">
                    <h1>Kevin{' '}
                        <br className="break"/>
                    Cendana</h1>
                    <p>C.S. Student</p>
                </div>
            </div>
            <div className  = "home__right">
                <div className  = "home__right-container">
                    <TypingDots />
                </div>
            </div>
        </div>
    );
}

export default Home;
