import React, { useState, useEffect, useRef } from 'react';
import './Slideshow.css';

const Slideshow = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const intervalRef = useRef(null);



  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  useEffect(() => {
    intervalRef.current = setInterval(nextSlide, 3000);
    return () => clearInterval(intervalRef.current);
  }, [currentSlide, slides.length]);

  // Function to determine slide position
  const getSlidePosition = (index) => {
    let position = 'nextSlide';
    if (index === currentSlide) {
      position = 'activeSlide';
    } else if ((index + 1) % slides.length === currentSlide) {
      position = 'prevSlide';
    }
    return position;
  };

  return (
    <div className="carousel">
      <div className="carousel-stage">
        {slides.map((slide, index) => (
          <figure 
            key={index}
            className={`carousel-slide ${getSlidePosition(index)}`}
            style={{ backgroundImage: `url(${slide.image})` }}
          >
            {slide.captions?.map((caption, captionIndex) => (
              <figcaption key={captionIndex} style={{ backgroundColor: caption.style.backgroundColor, color: caption.style.color }}>
                {caption.text}
              </figcaption>
            ))}
          </figure>
        ))}
      </div>
    </div>
  );
};

export default Slideshow;
