import React, { useState, useEffect, useRef } from 'react';

const TypingDots = () => {
  // Refs for managing timers
const timer1Ref = useRef(null);
const timer2Ref = useRef(null);

  // State for managing the display of elements
const [isTyping, setIsTyping] = useState(false);
const [messages, setMessages] = useState([]);

useEffect(() => {
    // Assuming homeContent is a state that manages whether the content is visible or not
    // This state should be passed as a prop to this component


    setIsTyping(true); // Start typing animation

    // Clear any existing timers
    clearTimeout(timer1Ref.current);
    clearTimeout(timer2Ref.current);

    // Start the typing animation with delays
    timer1Ref.current = setTimeout(() => {
      setIsTyping(false); // Stop typing animation
    setMessages(prevMessages => [...prevMessages, "Hello there! My name is Kevin."]);

timer2Ref.current = setTimeout(() => {
        setMessages(prevMessages => [
        ...prevMessages,
        "I'm currently a Senior in Computer Science at Sacramento State. Personally, I'm really into Web and App Development, but I really just like coding in general!",
        ]);
}, 1200);
    }, 1200);

    return () => {
      // Cleanup timers on component unmount
      clearTimeout(timer1Ref.current);
      clearTimeout(timer2Ref.current);
    };
  }, []); // Empty dependency array to mimic componentDidMount behavior

  return (
    <div id="allSpeechBubblesWrapper" style={{ display: isTyping ? '' : 'none' }}>
      {isTyping && <div className="typingDots">...</div>}
      {messages.map((message, index) => (
        <div key={index} className="speechBubble fadeInSlideUp newSpeechBubble">
          <p>{message}</p>
        </div>
      ))}
    </div>
  );
};

export default TypingDots;
