import React, { useEffect, useState } from 'react';

const TypingDots = () => {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowMessage(true);
    }, 1200); // 1200 milliseconds delay

    return () => clearTimeout(timer); // Cleanup the timer
  }, []);

  return (
    <div id="allSpeechBubblesWrapper" style={{ display: showMessage ? 'block' : 'none' }}>
      <div id="speechBubble" className="fadeInSlideUp">
        {/* Your message content goes here */}
      </div>
      <div id="typingDots" style={{ visibility: showMessage ? 'hidden' : 'visible' }}>
        {/* Your typing dots animation goes here */}
      </div>
    </div>
  );
};

export default TypingDots;
