// Import necessary hooks and dependencies from React
import React, { useState, useEffect, useRef } from 'react';
// Import the stylesheet for styling the slideshow
import './Slideshow.css';

// Slideshow component definition. This component takes 'slides' as a prop.
const Slideshow = ({ slides }) => {
  // State to keep track of the current slide
  const [currentSlide, setCurrentSlide] = useState(0);
  // State to track the previous slide for transition effects
  const [prevSlide, setPrevSlide] = useState(null);
  // Reference for the interval to enable automatic slide change
  const intervalRef = useRef(null);

  // Function to go to the next slide
  const nextSlide = () => {
    // Update the previous slide before changing the current slide
    setPrevSlide(currentSlide);
    // Change to the next slide, wrapping around if at the end
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  // Function to generate the style for each slide
  const getSlideStyle = (slide, index) => {
    // Default background image
    let backgroundImage = `url(${slide.image})`;

    // If the slide is a GIF and it's the current slide
    // Add a unique query string to reset the GIF animation
    if (slide.isGif && index === currentSlide) {
      const uniqueString = new Date().getTime();
      backgroundImage = `url(${slide.image}?unique=${uniqueString})`;
    }

    return { backgroundImage };
  };

  // useEffect hook to set up and clean up the interval for auto slide changing
  useEffect(() => {
    // Set an interval for the slide change
    intervalRef.current = setInterval(nextSlide, 3000);

    // Cleanup function to clear the interval when the component unmounts
    return () => clearInterval(intervalRef.current);
  }, [currentSlide, slides.length]);

  // Render the slideshow component
  return (
    <div className="slideshow">
      {/* Use optional chaining to ensure slides is an array */}
      {slides?.map((slide, index) => (
        <div
          key={index}
          className={`slide ${index === currentSlide ? 'active' : ''} ${index === prevSlide ? 'previous' : ''}`}
          style={{ backgroundImage: `url(${slide.image})` }}
        >
          {/* Use optional chaining to ensure captions is an array */}
          {slide.captions?.map((caption, captionIndex) => (
  <div 
    key={captionIndex}
    className="caption"
    style={{ backgroundColor: caption.style.backgroundColor, color: caption.style.color }}
  >
    {caption.text}
  </div>
))}

        </div>
      ))}
    </div>
  );

};

// Export the Slideshow component for use in other parts of the application
export default Slideshow;
