//--------------------------------------------------------------------------------------//
//                                         Home                                         //
//--------------------------------------------------------------------------------------//
import ReactRotatingText from 'react-rotating-text'; // Credit for rotating text animation: https://www.npmjs.com/package/react-rotating-text?activeTab=readme
import React, { useState, useEffect } from 'react';
import './Home.css';
import '../normalize.css';

function Home() {
    // State to keep track of the current index in the titles array
    const [currentIndex, setCurrentIndex] = useState(0);
    // Titles you want to rotate through
    const titles = ["Senior Software Engineer", "Web Developer", "Tech Enthusiast", "Creative Coder"];
    // State to control the animation
    const [animate, setAnimate] = useState(true);

    // This effect runs when the component mounts and sets up an interval
    useEffect(() => {
        const changeTitle = () => {
            // Turn off the animation
            setAnimate(false);

            // After a short delay, change the title and turn the animation on
            setTimeout(() => {
                setCurrentIndex(prevIndex => (prevIndex + 1) % titles.length);
                setAnimate(true);
            }, 250); // Short delay to ensure the title disappears before the next one appears
        };

        // Initial call to start the rotation
        changeTitle();

        // Set up an interval to change the title every 3.25 seconds (includes delay)
        const intervalId = setInterval(changeTitle, 3250);

        // Clear the interval when the component unmounts
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div className = "home " id = "home">
            <div className="home__left">
            <div className="home__text-container">
    <h1 className="home__text-name">Kevin Cendana</h1>
    {/* <p className={`home__text-alias ${animate ? 'animate-slide-up' : ''}`}>
    {titles[currentIndex]}
</p> */}

    <p className="home__text-alias">
        <ReactRotatingText items={['Computer Science Student', 'Web Developer', 'Enthusiastic Programmer']} />
    </p>
</div>

            </div>
            <div className="home__right">
                <div className="home-image-container"> </div>
            </div>
        </div>
    );
}

export default Home;
