//--------------------------------------------------------------------------------------//
//                                        App.js                                        //
//--------------------------------------------------------------------------------------//
/* main code for the website, where the routes (links) are defined and 
    the components (reusable code blocks) are imported */

import React, { } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';  
import './App.css';
import './normalize.css';
import Home from './Home/Home';

import About from './About/About';
import Contact from './Contact/Contact';
import Projects from './Projects/Projects';
import DarkModeToggle from './Navigationbar/DarkModeToggle/DarkModeToggle';
import NavigationBar from './NavigationBar/NavigationBar';

function App() {
  return (
    <Router>
        <div className="content-wrapper">
          <div className="navbar">
            {/*-- light / dark Mode Switch*/}
            <label className="switch">
              <DarkModeToggle />
              <span className="slider round"></span>
            </label>
          </div>
          <div className="main-content">
            {/* All components in a single page */}
            <Home />
            <Projects />
            <About />
            <Contact />
          </div>
        </div>
        <footer></footer>

    </Router>
  );
}


export default App;
