import React, { useState, useEffect } from 'react';
import './DarkModeToggle.css';

// Function to get a cookie by its name
const getCookie = (name) => {
    // Define the prefix for the cookie we are looking for
    const cookieName = name + "=";
    // Split the document's cookies into an array
    const cookieArray = document.cookie.split(';');

    // Iterate over the array to find our cookie
    for (let i = 0; i < cookieArray.length; i++) {
        let cookie = cookieArray[i];
        // Remove any leading whitespace
        while (cookie.charAt(0) === ' ') {
            cookie = cookie.substring(1);
        }
        // If our cookie is found, return its value
        if (cookie.indexOf(cookieName) === 0) {
            return cookie.substring(cookieName.length, cookie.length);
        }
    }
    // Return an empty string if cookie not found
    return "";
}

function DarkModeToggle() {
    console.log('DarkModeToggle(): called') // State to track if dark mode is enabled or not
    const [isDarkMode, setIsDarkMode] = useState(false); 

    // UseEffect hook to set the initial dark mode state based on the cookie value
    useEffect(() => {
        // Get the stored dark mode preference from the cookie
        const storedDarkMode = getCookie('dark-mode');
        // Update the state based on the stored value
        setIsDarkMode(storedDarkMode === '1');
        // Apply the dark mode class to the necessary elements
        toggleDarkMode(storedDarkMode === '1');
    }, []); // Empty dependency array means this effect runs once on mount

    // Function to set a cookie with a specified name, value, and expiration in days
    const setCookie = (name, value, days) => {
        console.log('DarkModeToggle(): setCookie() called')
        // Create a date for when the cookie will expire
        const date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        const expires = "expires=" + date.toUTCString();
        // Set the cookie
        document.cookie = name + "=" + value + ";" + expires + ";path=/";
    }

    // Function to toggle dark mode styling on or off
    const toggleDarkMode = (value) => {
        console.log('DarkModeToggle(): toggleDarkMode() called')
        // Toggle the 'dark-mode' class on the body
        document.body.classList.toggle('dark-mode', value);
        // Get all the elements that need dark mode styling
        const elements = document.querySelectorAll(
            'html, body, p, div, label, span, nav, Link, ul, h1, h2, .background, .navbar, .navbar__list, .navbar__link'
        );
        // Toggle the 'dark-mode' class on all of the selected elements
        elements.forEach(element => {
            element.classList.toggle('dark-mode', value);
        });
        setCookie('dark-mode', value ? '1' : '0', 30);
    }

    // Handler for the checkbox change event
    const handleToggleChange = () => {
        console.log('DarkModeToggle(): handleToggleChange() called')
        // Toggle the current dark mode state
        const newValue = !isDarkMode;
        // Update the state
        setIsDarkMode(newValue);
        // Save the new preference in a cookie
        setCookie('dark-mode', newValue ? '1' : '0', 30);
        // Apply/remove the dark mode styling
        toggleDarkMode(newValue);
    }

    return (
        // Render a checkbox to toggle dark mode on or off
        <>
            {/* Checkbox to toggle dark mode */}
            <input type="checkbox" id="dark-mode" checked={isDarkMode} onChange={handleToggleChange} />
        </>
    );
}

// Export the DarkModeToggle component and the getCookie function
export default DarkModeToggle;
export { getCookie };
