//--------------------------------------------------------------------------------------//
//                                Typing Dots Animation                                 //
//--------------------------------------------------------------------------------------//

function showTypingAnimation() {
  console.log("showTypingAnimation() called");



  if (homeContent.style.display !== 'block') {
    return; // Exit the function if homeContent is not visible
  }
  // let timer1, timer2;
  const allSpeechBubblesWrapper = document.getElementById('allSpeechBubblesWrapper');
  const firstMessageDelay = 1200; // 1000 = 1 second

  document.querySelector(".allSpeechBubblesWrapper");

  // Clear any existing timers if they exist
  console.log("showTypingAnimation(): clearing timers");
  if (timer1) clearTimeout(timer1);
  if (timer2) clearTimeout(timer2);

  // Show allSpeechBubblesWrapper and typingDots
  console.log("showTypingAnimation(): showing allSpeechBubblesWrapper and typingDots");
  allSpeechBubblesWrapper.style.display = "";
  speechBubble.style.visibility = "visible";  
  typingDots.style.visibility = "visible";
  typingDots.style.opacity = "1";



  // After the duration you've set (e.g., 3 seconds), hide the dots, remove the container, and show the message
  timer1 = setTimeout(() => {
    console.log("showTypingAnimation(): timer1 called");
    // hide dots
    typingDots.style.opacity = "0";
    typingDots.style.visibility = "hidden";

    // Create and show the first message
    const newMessage = createMessage("Hello there! My name is Kevin.");
    document.querySelector(".allSpeechBubblesWrapper").appendChild(newMessage);



    // Wait a second and then show another message
    timer2 = setTimeout(() => {
      console.log("showTypingAnimation(): timer2 called");
      const secondMessage = createMessage("I'm currently a Senior in Computer Science at Sacramento State. Personally, I'm really into Web and App Development, but I really just like coding in general!");
      document.querySelector(".allSpeechBubblesWrapper").appendChild(secondMessage);
    }, 1200);
  }, firstMessageDelay);
}

function createMessage(text) {
  console.log("createMessage() called");
  newMessage = document.createElement("div");
  newMessage.className = "speechBubble fadeInSlideUp newSpeechBubble";
  newMessage.style.opacity = "0";  // Initial state as invisible
  newMessage.style.display = "block";


  const newText = document.createElement("p");
  newText.textContent = text;
  newMessage.appendChild(newText);

  // Add a slight delay before starting the fade-in and slide-in effects to ensure they don't start prematurely.
  setTimeout(() => {
    newMessage.style.opacity = "1";
    newMessage.style.transform = "translateY(0)";
  }, 50); // Increased to 50ms for safer timing

  return newMessage;
}

function eraseMessage() {

  //Remove the typing dots container from the layout
  if (typingDots) {
      typingDots.style.visibility = "hidden";
      typingDots.style.opacity = "0";
  }

  // Remove all new speech bubbles
  const newSpeechBubbles = document.querySelectorAll('.newSpeechBubble');
  newSpeechBubbles.forEach(bubble => {
    bubble.remove();
  });


  if (allSpeechBubblesWrapper) {
    allSpeechBubblesWrapper.style.display = "none"; 
  }  
}

export default TypingDots;
