//--------------------------------------------------------------------------------------//
//                                         Home                                         //
//--------------------------------------------------------------------------------------//
// this is the default home page of the website, the first page that the user sees

import React, { useEffect } from 'react';
import './Home.css';
import '../App.css';
import './TypingDots.css';
import '../normalize.css';
import TypingDots from './TypingDots';
import { AnimateLines } from './AnimateLines';

function Home() {
    const [showHomeContent, setShowHomeContent] = React.useState(true); //state for managing the display of typing dots

    useEffect(() => {
        // Trigger the line animation when the component mounts
        // AnimateLines('.home__left-image-container');
    }, []);

    const Feature = ({ title, description, image }) => {
        return (
            <div className="home__feature">
                <div className="home__feature-image" style={{ backgroundImage: `url(${image})` }}></div>
                <h3 className="home__feature-title">{title}</h3>
                <p className="home__feature-description">{description}</p>
            </div>
        );
    };


    return (
        <div className="home">
                <section className="home__left animated">
                    <div class="home__left-wrapper">
                        <div className="home__left-image-container"> </div>
                        <div className="home__text-container">
                            <h1 className = "home__text-name">Kevin
                            Cendana</h1>
                            <p className = "home__text-alias">Computer Science Student</p>
                        </div>
                    </div>
                </section>
            <section className  = "home__right">
                <div className  = "home__right-container">
                    <TypingDots />
                </div>
            </section>
            <section className = "home__bottom">

            </section>
        </div>
    );
}

export default Home;
