import React, { useState, useEffect } from 'react';
import './Home.css';
import '../normalize.css';

function Home() {
    // State to keep track of the current index in the titles array
    const [currentIndex, setCurrentIndex] = useState(0);
    // Titles you want to rotate through
    const titles = ["Senior Software Engineer", "Web Developer", "Tech Enthusiast", "Creative Coder"];
    // State to control the animation
    const [animate, setAnimate] = useState(false);

    // This effect runs when the component mounts and sets up an interval
    useEffect(() => {
        const intervalId = setInterval(() => {
            // First, turn off the animation
            setAnimate(false);

            // After a short delay, change the title and turn the animation on
            setTimeout(() => {
                setCurrentIndex(prevIndex => (prevIndex + 1) % titles.length);
                setAnimate(true);
            }, 250); // Short delay to allow the title to disappear before the next one appears

        }, 3000); // Change every 3000 milliseconds (3 seconds)

        // Clear the interval when the component unmounts
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div className="home" id="home">
            <div className="home__left">
                <div className="home__text-container">
                    <h1 className="home__text-name">Kevin Cendana</h1>
                    <p className={`home__text-alias ${animate ? 'animate-slide-up' : ''}`}>
                        {titles[currentIndex]}
                    </p>
                </div>
            </div>
            <div className="home__right">
                <div className="home-image-container"></div>
            </div>
        </div>
    );
}

export default Home;
