//--------------------------------------------------------------------------------------//
//                                 NavigationBar.js                                     //
//--------------------------------------------------------------------------------------//

import React, { useState } from 'react';
import { Link } from 'react-scroll';
import classNames from 'classnames';
import './NavigationBar.css';
import DarkModeToggle from './DarkModeToggle/DarkModeToggle';

function NavigationBar() {
    const [activeSection, setActiveSection] = useState('');

    // Function to handle active section
    const handleSetActive = (to) => {
        setActiveSection(to);

        // Get the class name for the active section
        const activeClass = getActiveClass(to);

        // Check if there is a valid class name
        if (activeClass) {
            // Construct the selector with the valid class name
            const selector = `.navbar__list li.${activeClass}`;

            // Add the temporary active class
            const element = document.querySelector(selector);
            if (element) {
                element.classList.add('temp-active');

                // Remove the class after __ milliseconds
                setTimeout(() => {
                    element.classList.remove('temp-active');
                }, 300);
            }
        }
    };


    // Helper function to apply active class conditionally
    const getActiveClass = (section) => {
        return classNames({ 'navbar__link-active': activeSection === section });
    };


    return (
        <nav className="navbar">
            <div className="navbar__list-wrapper">
                <ul className="navbar__list">
                <li className={getActiveClass('home')}>
    <Link to="home" spy={true} smooth={true} duration={0} onClick={() => handleSetActive('home')}>
        Home
    </Link>
</li>

<li className={getActiveClass('about')}>
    <Link to="about" spy={true} smooth={true} duration={0} onClick={() => handleSetActive('about')}>
    About
    </Link>
</li>
<li className={getActiveClass('projects')}>
    <Link to="projects" spy={true} smooth={true} duration={0} onClick={() => handleSetActive('projects')}>
    Projects
    </Link>
</li>
<li className={getActiveClass('contact')}>
    <Link to="contact" spy={true} smooth={true} duration={0} onClick={() => handleSetActive('contact')}>
    Contact
    </Link>
</li>
                </ul>
            </div>
            <div className="top-right-items">
            <a href="https://github.com/KevCendana" target="_blank" className="github-logo-link"rel="noreferrer">
                <div className = "github-logo"></div>
            </a>
                <div className="toggle__container">
                    <label className="switch">
                        <DarkModeToggle />
                        <span className="slider round"></span>
                    </label>
                </div>
            </div>
        </nav>
    );
}

export default NavigationBar;
