// animateLines.js
import { gsap } from 'gsap';



export const AnimateLines = (containerSelector, circleRadius) => {
    const container = document.querySelector(containerSelector);
    const numberOfLines = 8;
    const angleIncrement = 360 / numberOfLines;

    for (let i = 0; i < numberOfLines; i++) {
      const line = document.createElement('div');
      line.classList.add('line');

      // Calculate angle in radians
      const angle = i * angleIncrement * (Math.PI / 180);

      // Set the initial rotation and translation of the line
      line.style.transform = `rotate(${i * angleIncrement}deg) translate(${circleRadius}px)`;

      container.appendChild(line);

      // GSAP animation
      gsap.fromTo(line,
        {
          scale: 0,
          transformOrigin: '0% 100%', // origin at the bottom center of the line
        },
        {
          scale: 1,
          opacity: 1,
          duration: 0.5,
          ease: 'elastic.out(1, 0.3)',
          onComplete: () => gsap.to(line, { opacity: 0, duration: 0.5 })
        }
      );
    }
  };
