//--------------------------------------------------------------------------------------//
//                                 NavigationBar.js                                     //
//--------------------------------------------------------------------------------------//

import React, { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import './NavigationBar.css';
import DarkModeToggle from './DarkModeToggle/DarkModeToggle';

function NavigationBar() {
    const [activeSection, setActiveSection] = useState('');

    useEffect(() => {
        const handleScroll = () => {
            // Get all sections with an ID defined
            const sections = document.querySelectorAll('section[id]');
            let activeSection = '';

            // Determine which section is currently in view
            sections.forEach(section => {
                const sectionTop = section.offsetTop;
                const sectionHeight = section.clientHeight;
                if (window.scrollY >= sectionTop - sectionHeight / 3) {
                    activeSection = section.getAttribute('id');
                }
            });

            // Set the active section state
            setActiveSection(activeSection);
        };

        // Attach the event listener
        window.addEventListener('scroll', handleScroll);

        // Clean up the event listener
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <nav className="navbar">
            <ul className="navbar__list">
                <li className={activeSection === 'home' ? 'navbar__link-active' : ''}>
                    <Link to="home" spy={true} smooth={true} duration={500}>Home</Link>
                </li>
                <li className={activeSection === 'about' ? 'navbar__link-active' : ''}>
                    <Link to="about" spy={true} smooth={true} duration={500}>About</Link>
                </li>
                <li className={activeSection === 'projects' ? 'navbar__link-active' : ''}>
                    <Link to="projects" spy={true} smooth={true} duration={500}>Projects</Link>
                </li>
                <li className={activeSection === 'contact' ? 'navbar__link-active' : ''}>
                    <Link to="contact" spy={true} smooth={true} duration={500}>Contact</Link>
                </li>
            </ul>

            <div className="navbar__toggle">
                <DarkModeToggle />
            </div>
        </nav>
    );
}

export default NavigationBar;
