import './About.css'; 
import personal_project1 from '../images/project_thumbnails/maplestory_app_cropped.gif';
import personal_project2 from '../images/project_thumbnails/invaded_space.gif';
import personal_project3 from '../images/project_thumbnails/word_scrabble.png';


// Define your slides here
const slides = [
  {
    image: personal_project1,
    caption: 'Making Maplestory w/ Flutter',
  },
  {
    image: personal_project2,
    caption: 'Invaded Space',
  },
  {
    image: personal_project2,
    caption: 'Invaded Space',
  },
];

function About() {
  return (
    <div className="about">
      <h1>About Us</h1>
      <div class="about__slideshow-container">
        <Slideshow slides={slides} />
      </div> {/* Add the Slideshow component here */}
      {/* ...other content */}
    </div>
  );
}

export default About;
