//--------------------------------------------------------------------------------------//
//                                 NavigationBar.js                                     //
//--------------------------------------------------------------------------------------//

import React, { useState } from 'react';
import { Link } from 'react-scroll';
import classNames from 'classnames';
import './NavigationBar.css';
import DarkModeToggle from './DarkModeToggle/DarkModeToggle';

function NavigationBar() {
    const [activeSection, setActiveSection] = useState('');

    // Function to handle active section
    const handleSetActive = (to) => {
        setActiveSection(to);
    };

    // Helper function to apply active class conditionally
    const getActiveClass = (section) => {
        return classNames({ 'navbar__link-active': activeSection === section });
    };

    return (
        <nav className="navbar">
            <ul className="navbar__list">
                <li className={getActiveClass('home')}>
                    <Link to="home" spy={true} smooth={true} duration={500} onSetActive={() => handleSetActive('home')}>
                        Home
                    </Link>
                </li>
                <li className={getActiveClass('about')}>
                    <Link to="about" spy={true} smooth={true} duration={500} onSetActive={() => handleSetActive('about')}>
                        About
                    </Link>
                </li>
                <li className={getActiveClass('projects')}>
                    <Link to="projects" spy={true} smooth={true} duration={500} onSetActive={() => handleSetActive('projects')}>
                        Projects
                    </Link>
                </li>
                <li className={getActiveClass('contact')}>
                    <Link to="contact" spy={true} smooth={true} duration={500} onSetActive={() => handleSetActive('contact')}>
                        Contact
                    </Link>
                </li>
                <li></li>
            </ul>

            <label className="switch">
              <DarkModeToggle />
              <span className="slider round"></span>
            </label>
        </nav>
    );
}

export default NavigationBar;
