//--------------------------------------------------------------------------------------//
//                                       Projects.js                                       //
//--------------------------------------------------------------------------------------//


import React from 'react';
// import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Import carousel CSS
import './Projects.css'; // Import your CSS file for styling
import project_data from './ProjectsData'; // Import your project data

function Projects() {
    return (
        <div className="projects" id="projects">
            <div className="projects__header">
                <h1>Projects</h1>
                <a href="https://github.com/KevCendana" target="_blank" className="github-logo-link" rel="noreferrer">
                    <div className="github-logo"></div>
                </a>
            </div>

            <div className="projects__section-wrapper">
                {/* Carousel for projects */}
                <Carousel showArrows={true} autoPlay={true} infiniteLoop={true}>
                    {project_data.map((item, index) => (
                        <div key={index}>
                            <img src={item.image} alt={`Slide ${index + 1}`} />
                            <p className="legend">
                                {/* Render captions */}
                                {item.captions.map((caption, captionIndex) => (
                                    <span key={captionIndex} style={caption.style}>{caption.text}</span>
                                ))}
                            </p>
                        </div>
                    ))}
                </Carousel>
            </div>
        </div>
    );
}

export default Projects;
