//--------------------------------------------------------------------------------------//
//                                        App.js                                        //
//--------------------------------------------------------------------------------------//
/* main code for the website, where the routes (links) are defined and 
    the components (reusable code blocks) are imported */

import React, { } from 'react';
import { BrowserRouter as Router, Route, NavLink, Routes } from 'react-router-dom';  
import './App.css';
import './normalize.css';
import Home from './Home/Home';
import TypingDots from './Home/TypingDots';
import About from './About/About';
import Contact from './Contact/Contact';
import DarkModeToggle from './DarkModeToggle/DarkModeToggle';

function App() {
  return (
    <Router>
      <div className = "background">
        <div class="|">
          <div className="navbar">
            {/*-- light / dark Mode Switch*/}
            <label className="switch">
              <DarkModeToggle />
              <span className="slider round"></span>
            </label>
            <ul className="navbar__list">
            <li>
              <NavLink to="/" className={({ isActive }) => isActive ? "navbar__link navbar__link-active" : "navbar__link"}>
                Home
              </NavLink>
            </li>
            <li>
              <NavLink to="/about" className={({ isActive }) => isActive ? "navbar__link  navbar__link-active" : "navbar__link"}>
                About
              </NavLink>
            </li>
            <li>
              <NavLink to="/contact" className={({ isActive }) => isActive ? "navbar__link  navbar__link-active" : "navbar__link"}>
                Contact
              </NavLink>
            </li>
          </ul>
          </div>
          <div className="main-content">
          {/* defining the routes (URL paths) and their components*/}
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/DarkModeToggle" element={<DarkModeToggle />} />
            </Routes>
          </div>
        </div>
      <footer></footer>
      </div>
    </Router>
  );
}

export default App;
