import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';  
import './App.css';
import Home from './Home/Home';
import About from './About/About';
import Contact from './Contact/Contact';
import DarkModeToggle, { getCookie } from './DarkModeToggle/DarkModeToggle';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const storedDarkMode = getCookie('dark-mode'); 
    setIsDarkMode(storedDarkMode === '1');
    document.body.classList.toggle('dark-mode', storedDarkMode === '1');
  }, []);

  return (
    <Router>
      <div className="background">
        <div className="navbar">
          <label className="switch">
            <input type="checkbox" 
                   checked={isDarkMode} 
                   onChange={() => {
                     const newValue = !isDarkMode;
                     setIsDarkMode(newValue);
                     document.body.classList.toggle('dark-mode', newValue);
                   }}
            />
            <span className="slider round"></span>
          </label>
          <ul className="navbar__list">
            <Link to="/" className="navbar__link">Home</Link>
            <Link to="/about" className="navbar__link">About</Link>
            <Link to="/contact" className="navbar__link">Contact</Link>
          </ul>
        </div>
        <div className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/DarkModeToggle" element={<DarkModeToggle />} />
          </Routes>
        </div>
        <footer></footer>
      </div>
    </Router>
  );
}

export default App;
