// import the main react dependency and the components
import React, { useState, useEffect } from 'react';

import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';  

import './App.css';
import Home from './Home/Home';

import About from './About/About';
import Contact from './Contact/Contact';
import DarkModeToggle from './DarkModeToggle/DarkModeToggle';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    // Check the dark mode state from cookies or local storage here
    const storedDarkMode = getCookie('dark-mode'); 
    setIsDarkMode(storedDarkMode === '1');
    // Apply or remove the dark mode class to the body here
    document.body.classList.toggle('dark-mode', storedDarkMode === '1');
  }, []);
  return (
    <Router> {/* use the router component to enable client-side routing */}
      <div className = "background">
        <div className="navbar">
          {/*-- Light / Dark Mode Switch*/}
          <label className="switch">
            <DarkModeToggle />
            <span className="slider round"></span>
          </label>
          <ul className="navbar__list">
            <Link to="/" className="navbar__link">Home</Link>
            <Link to="/about" className="navbar__link">About</Link>
            <Link to="/contact" className="navbar__link">Contact</Link>
          </ul>
        </div>
        <div className="main-content">
        {/* define the routes (URL paths) and their components*/}
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/DarkModeToggle" element={<DarkModeToggle />} />
          </Routes>
        </div>
      <footer></footer>
      </div>
    </Router>
  );
}

export default App;
