import React, { useState, useEffect } from 'react'; 
import './DarkModeToggle.css'; 
import { getCookie, setCookie } from './CookieUtils'; 
import { useLocation } from 'react-router-dom'; 

function DarkModeToggle() {
    // use location to detect route changes, location is an object that has a pathname property
    const location = useLocation();                      // detects route changes
    const [isDarkMode, setIsDarkMode] = useState(false); // track dark mode state

    // get dark mode preference from cookie
    const getStoredDarkMode = () => getCookie('dark-mode') === 'true';

    useEffect(() => {
        setIsDarkMode(getStoredDarkMode());            // initialize dark mode based on cookie
        if (getStoredDarkMode()) toggleDarkMode(true); // apply styles if dark mode
    }, [location]);                                    // rerun effect on route change

    // store dark mode preference in cookie
    const storeDarkMode = (value) => setCookie('dark-mode', value, 365);

    // toggle dark mode on / off
    const toggleDarkMode = (value) => {
        // update body class
        document.body.classList.toggle('dark-mode', value);

        // toggle styles for selected elements
        const selectors = 'html, body, p, div, label, span, nav, Link, ul, h1, h2, .background, .navbar, .navbar__list, .navbar__link';
        document.querySelectorAll(selectors).forEach(el => el.classList.toggle('dark-mode', value));

        // update the cookie
        storeDarkMode(value); 
    }

    // handle the checkbox toggle
    const handleToggleChange = () => {
        const newValue = !isDarkMode; // get opposite of current state
        setIsDarkMode(newValue);      // update state
        toggleDarkMode(newValue);     // toggle styles
    }

    //render checkbox
    return (
        <input type="checkbox" id="dark-mode" checked={isDarkMode} onChange={handleToggleChange} /> 
    );
}

export default DarkModeToggle;
