// Slideshow.js

import React from 'react';

// The main component
const ProjectSlideshow = () => {
  return (
    <body>
      {/* Radio buttons for each project */}
      {project_data.map((project, index) => (
        <input 
          type="radio" 
          name="position" 
          key={index} 
          defaultChecked={index === 0} 
        />
      ))}

      {/* Main carousel area */}
      <main id="carousel">
        {/* Individual slide items */}
        {project_data.map((project, index) => (
          <div 
            className="item" 
            key={index} 
            style={{ 
              backgroundImage: `url(${project.image})`,
              // Add other styles here if needed
            }}
          >
            {/* Project captions */}
            {project.captions && project.captions.map((caption, captionIndex) => (
              <span 
                key={captionIndex} 
                style={caption.style}
              >
                {caption.text}
              </span>
            ))}
          </div>
        ))}
      </main>
    </body>
  );
}

export default ProjectSlideshow;
