import React, { useState, useEffect } from 'react'; // Importing required libraries
import './DarkModeToggle.css'; // Importing styles
import { getCookie, setCookie } from './CookieUtils'; // Importing utility functions
import { useLocation } from 'react-router-dom';

function DarkModeToggle() {
    console.log('DarkModeToggle(): called');
    const location = useLocation();
    // State to track if dark mode is enabled or not
    const [isDarkMode, setIsDarkMode] = useState(false);

    // Function to get the stored dark mode preference from localStorage
    const getStoredDarkMode = () => getCookie('dark-mode') === 'true';

    useEffect(() => {
        const currentDarkMode = getStoredDarkMode();
        setIsDarkMode(getStoredDarkMode()); // Update the state based on the stored value

        // If dark mode is enabled, apply dark mode to all elements.
        if (currentDarkMode) {
            toggleDarkMode(true);
        }        
    }, []); // This effect runs once on mount

    // Function to store dark mode preference in localStorage
    const storeDarkMode = (value) => {
        setCookie('dark-mode', value, 365);
    };

    // Function to toggle dark mode styling on or off
    const toggleDarkMode = (value) => {
        console.log('DarkModeToggle(): toggleDarkMode() called');
        document.body.classList.toggle('dark-mode', value); // Toggle the 'dark-mode' class on the body

        // Toggle the 'dark-mode' class on all of the selected elements
        const elements = document.querySelectorAll(
            'html, body, p, div, label, span, nav, Link, ul, h1, h2, .background, .navbar, .navbar__list, .navbar__link'
        );
        elements.forEach(element => {
            element.classList.toggle('dark-mode', value);
        });

        storeDarkMode(value); // Store the dark mode preference
    }

    // Handler for the checkbox change event
    const handleToggleChange = () => {
        console.log('DarkModeToggle(): handleToggleChange() called');

        const newValue = !isDarkMode; // Toggle the current dark mode state

        setIsDarkMode(newValue); // Update the state
        toggleDarkMode(newValue); // Apply/remove the dark mode styling

    }

    return (
        <>
            {/* Checkbox to toggle dark mode */}
            <input type="checkbox" id="dark-mode" checked={isDarkMode} onChange={handleToggleChange} /> 
        </>
    );
}

export default DarkModeToggle; // Exporting the DarkModeToggle component
